mapper.select:
    
    	select * from (
    		select /* + INDEX_DESC(bs_author SYS_C008327 */
    			rownum as rn, authorId, authorName, nationId, regDate, updateDate
    		from bs_author
    		where
    
	    	
	    		authorName like '%'||#{keyword}||'%' and
	    	
    
    		rownum <= #{pageNum}*#{amount}
    		)
    	where rn > (#{pageNum} -1) * #{amount}
