mapper.select:
	
	    select * from (
	        select /*+INDEX_DESC(bs_book 인덱스ID)*/ rownum as rn, bookId, bookName,
	            (select authorName from bs_author where bs_book.authorId = bs_author.authorId) authorName, authorId,
	            (select cateName from bs_bcate where bs_book.cateCode = bs_bcate.cateCode) cateName,cateCode, publisher, publeYear, bookPrice  
	        from bs_book
	        where 
    
    
    
    
      
	        rownum <= #{pageNum} * #{amount} 
        	)
        where rn > (#{pageNum} -1) * #{amount}
